import { useEffect, useState } from 'react';
import { Container } from 'react-bootstrap';
import data from './characters.json';

export function Characters() {
  const [loading, setLoading] = useState(false);
  const [character, setCharacter] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      //Keep commented to use local json file.
      // const rawCharacters = await fetch('https://the-one-api.dev/v2/character/?_id=5cd99d4bde30eff6ebccfc2c', { headers: headers });
      // const characters = await rawCharacters.json();

      const c = data.docs.map ((array) => (
        <div key={array._id}>
          {array.name}
        </div>
      ));

      setCharacter(c);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Characters">
      <h1>Characters</h1>
      <Container>
        {character}
      </Container>
    </main>
  );
}
