import { useEffect, useState } from 'react';
import { Container, Accordion, Row, Col } from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState();
  const [book, setBook] = useState();

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchDataBook = async (idbook) => {
      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      //Keep commented to use local json file.
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=' + idbook, { headers: headers });
      // const chapters = await rawChapters.json();

      const c = data.docs.map(array => (
        <div id={array.book} key={array._id}>
          {array.chapterName}
        </div>
      ));
      setChapter(c);

      const b = books.docs.map((element, id) => (
        <Accordion key={id}>
          <Accordion.Item key={element._id}>
            <Accordion.Header>{element.name}</Accordion.Header>
            <Accordion.Body>
              {chapter}
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      ));
      setBook(b);

      setLoading(false);
    };

    fetchDataBook();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {book}

      </Container>
    </main>
  );
}
