import { useEffect, useState } from 'react';
import { Container, Row, Col } from "react-bootstrap";

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=5cf5805fb53e011a64671582', { headers: headers });
      const chapters = await rawChapters.json();

      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      const b = books.docs.map(groupMap => groupMap);

      // const c = chapters.docs.map ((array) => (
      //   <Row key={array._id}>
      //     <Col>
      //       {array.chapterName}
      //     </Col>
      //     <Col>
      //       {array.book}
      //     </Col>
      //   </Row>
      // ));

      const c = chapters.docs.map(array => {
        b.forEach(element => {
          if (element._id == array.book)
          {
            <Row key={array._id}>
            <Col>
              {array.chapterName}
            </Col>
            <Col>
              {element.name}
            </Col>
          </Row>
          }
        });
      }

      );

      setChapter(c);

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}
      </Container>
    </main>
    );
}
