import { Col, Container, Row } from "react-bootstrap";

export function Characters() {
  const [loading, setLoading] = useState(false);
  const [character, setCharacter] = useState();

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      const rawCharacters = await fetch('https://the-one-api.dev/v2/character?_id=' + q.character, { headers: headers })
      const characters = await rawCharacters.json();
      const c = characters.docs[0];
      setCharacter(c.name)

      console.log(chars.docs[0]);
      // const char = chars.docs.map ((array) => (
      //   <Col key={array._id}>
      //     <Row>
      //       {array.name}
      //     </Row>
      //   </Col>
      // ));

      setCharacter(c);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <main id="Characters">
        <h1>Characters</h1>
        <Container>
          {character}
        </Container>
      </main>
    );
  }
}
