import { useEffect, useState } from 'react';
import { Container, Accordion, Row, Col } from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);
  const [book, setBook] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=5cf5805fb53e011a64671582', { headers: headers });
      // const chapters = await rawChapters.json();

      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      const c = data.docs.map((array, id) => {
        const b = books.docs.map((element, id) => {
          if (element._id === array.book) {
            return (
                <div id='children' key={array._id}>
                  {array.chapterName}
                </div>
            );
          }
        } );
        return b;
      });

      setChapter(c);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}

      </Container>
    </main>
  );
}
