import { useEffect, useState } from 'react';
import { Container, Row, Col } from "react-bootstrap";

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/', { headers: headers });
      const chapters = await rawChapters.json();

      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      const b = books.docs.map(groupMap => groupMap._id);
      console.log("It initiated:", b);

      const c = chapters.docs.map(groupChapter => {
        const { chap } = groupChapter;

        if (b.includes(chap._id)) {
          console.log(chap);
          return chap;
        }
      });
      console.log("It ended rendering!", c);
      // const c = chapters.docs.map ((arrayC) (
      //   books.docs.map ((arrayB) 
      //     return (
      //       <Row key={arrayC._id}>
      //         <Col>
      //           {arrayC.chapterName}
      //         </Col>
      //         <Col>
      //           {arrayB.name}
      //         </Col>
      //       </Row>
      //     );
      //   }, {});
      // }, {});

      setChapter(c);

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}
      </Container>
    </main>
    );
}
