import { useEffect, useState } from 'react';
import { Container, Accordion, Row, Col } from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);
  const [book, setBook] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=' + idbook, { headers: headers });
      // const chapters = await rawChapters.json();

      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      // const c = data.docs.map((array, id) => {
      //   const b = books.docs.map((element, id) => {
      //     if (element._id === array.book) {
      //       return (
      //         <div id='parent' key={element._id}>
      //           {element.name}
      //           <div id='children' key={array._id}>
      //             {array.chapterName}
      //           </div>
      //         </div>
      //       );
      //     }
      //   } );
      //   return b;
      // });

      const c = data.docs.map((array, id) => (
        <div id='children' key={array._id}>
          {array.chapterName}
        </div>
      ));
      const b = books.docs.map((element, id) => (
        <Accordion key={id}>
          <Accordion.Item key={element._id}>
            <Accordion.Header>{element.name}</Accordion.Header>
            <Accordion.Body>
              {c}
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      ));

      setBook(b);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {book}

      </Container>
    </main>
  );
}
