import { useEffect, useState } from 'react';
import { Container } from 'react-bootstrap';

export function Books() {
  const [book, setBook] = useState();
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    const fetchData = async () => {
      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      console.log(rawBooks);
      const books = await rawBooks.json();
      console.log(books.docs);
      const book = books.docs[0];
      setBook(book.name);

      // for (var key in books) {
      //   console.log(key);
      // }

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center><img src='../assets/spinner.svg' width="80" height="80" alt='Loading...' /></center>
    );
  }

  return (
    <main id="Books">
      <Container>
        <h1>Books</h1>
        {book}
      </Container>
    </main>
  );
}
