import '../assets/chapters.css';
import { useEffect, useState } from 'react';
import { Container, Accordion} from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [book, setBook] = useState();

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchDataBook = async (idbook) => {
      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      //Keep commented to use local json file.
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter, { headers: headers });
      // const chapters = await rawChapters.json();

      const b = books.docs.map((element, id) => (
        <Accordion defaultActiveKey="0" key={id}>
          <Accordion.Item key={element._id}>
            <Accordion.Header>{element.name}</Accordion.Header>
            <Accordion.Collapse>
            <Accordion.Body>
              {data.docs.map((array, id2) => {
                let c = null;
                if (element._id === array.book) {
                  c = (
                    <div id={array.book} key={array._id}>
                      {id2 + 1} - {array.chapterName}
                    </div>
                  );
                }
                return c;
              })}
            </Accordion.Body>
            </Accordion.Collapse>
          </Accordion.Item>
        </Accordion>
      ));
      setBook(b);
      setLoading(false);
    };

    fetchDataBook();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container className='text-center'>
        {book}
      </Container>
    </main>
  );
}
