import { useEffect, useState } from 'react';
import { Container } from "react-bootstrap";

export function Movies() {
  const [loading, setLoading] = useState(false);
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      const rawMovies = await fetch('https://the-one-api.dev/v2/movie', {
        headers: headers
      });
      const movies = await rawMovies.json();
      const m = movies.docs.map ((array) => (
        <div key={array._id}>
          {array.name}
        </div>
        ));

        setMovie(m);
        setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }
    return (
      <main id="Movies">
        <h1>Movies</h1>
        <Container>
          {movie}
        </Container>
      </main>
    );
  }
