import { useEffect, useState } from 'react';
import { CardGroup, Card, Container, Pagination } from 'react-bootstrap';
import data from './e39bb0d6-2686-4edf-83fb-26915ea99483.json';

export function Characters() {
  const [loading, setLoading] = useState(false);
  const [character, setCharacter] = useState(null);
  let active = 2;
  let items = [];
  for (let number = 1; number <= 5; number++) {
    items.push(
      <Pagination.Item key={number} active={number === active}>
        {number}
      </Pagination.Item>,
    );
  }

  const paginationBasic = (
    <div className='justify-content-center'>
      <Pagination>{items}</Pagination>
    </div>
  );

  useEffect(() => {
    setLoading(true);
    // const headers = {
    //   'Accept': 'application/json',
    //   'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    // };
    const fetchData = async () => {
      //Keep commented to use local json file.
      // const rawCharacters = await fetch('https://the-one-api.dev/v2/character/?_id=5cd99d4bde30eff6ebccfc2c', { headers: headers });
      // const characters = await rawCharacters.json();

      const c = data.docs.map ((array, id) => {
        console.log(array);

        return (
            <Card key={array._id}>
              <Card.Title className='text-center'>{array.name}</Card.Title>
              <Card.Body>
                <Card.Text>
                  Race: {array.race}<br />
                  Gender: {array.gender}<br />
                  Birth: {array.birth}<br />
                  Spouse: {array.spouse}<br />
                  Death: {array.death}<br />
                  WikiUrl: <Card.Link href={array.wikiUrl} target="_blank">{array.wikiUrl}</Card.Link>
                </Card.Text>
              </Card.Body>
            </Card>
        );
      });

      setCharacter(c);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Characters" className='bg-black'>
      <h1 className='text-light'>Characters</h1>
      <Container fluid>
        <CardGroup>
          {character}
        </CardGroup>

        {paginationBasic}
      </Container>
    </main>
  );
}
