            //     <Row key={arrC._id}>
            //     <Col>
            //       {arrC.chapterName}
            //     </Col>
            //     <Col>
            //       {arrB.name}
            //     </Col>
            //   </Row>
            // );
          }
        }
      }
      // const c = chapters.docs.map ((arrayC) (
      //   books.docs.map ((arrayB) 
      //     return (
      //       <Row key={arrayC._id}>
      //         <Col>
      //           {arrayC.chapterName}
      //         </Col>
      //         <Col>
      //           {arrayB.name}
      //         </Col>
      //       </Row>
      //     );
      //   }, {});
      // }, {});
      const c = "Chapter 1";
      setChapter(c);

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}
      </Container>
    </main>
    );
}
