      //     <Col>
      //       {array.chapterName}
      //     </Col>
      //     <Col>
      //       {array.book}
      //     </Col>
      //   </Row>
      // ));

      // const c = chapters.docs.map(array => {
      //   b.forEach(element => {
      //     if (element._id == array.book) {
      //       console.log(array.chapterName, element.name);
      //       return (
      //       <Row key={array._id}>
      //         <Col>
      //           {array.chapterName}
      //         </Col>
      //         <Col>
      //           {element.name}
      //         </Col>
      //       </Row>
      //       );
      //     }
      //   });
      // });
      const c = chapters.docs.map(array => {
        b.map(element => {
          if (element._id == array.book) {
            console.log(element);
            array.book = element.name;
            console.log(array);
          }
        });
      });
      console.log(c);

      setChapter(c);

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}
      </Container>
    </main>
    );
}
