import '../assets/characters.css';
import { useEffect, useState } from 'react';
import { FormControl, Form, CardGroup, Card, Container, Pagination, Button } from 'react-bootstrap';
import data from './e39bb0d6-2686-4edf-83fb-26915ea99483.json';
import LOTRImg from '../assets/tree-bottom.png';

export function Characters() {
  const [loading, setLoading] = useState(false);
  const [character, setCharacter] = useState(null);
  let active = 2;
  let items = [];
  for (let number = 1; number <= 5; number++) {
    items.push(
      <Pagination.Item key={number} active={number === active}>
        {number}
      </Pagination.Item>,
    );
  }

  const paginationBasic = (
    <div className='justify-content-center'>
      <Pagination>{items}</Pagination>
    </div>
  );

  useEffect(() => {
    setLoading(true);
    const fetchData = async () => {
      //Keep commented to use local json file.
      // const rawCharacters = await fetch('https://the-one-api.dev/v2/character/?_id=5cd99d4bde30eff6ebccfc2c', { headers: headers });
      // const characters = await rawCharacters.json();

      const c = data.docs.map ((array, id) => (
            <Card key={array._id}>
              <Card.Link href={array.wikiUrl} target="_blank">
              <Card.Title>{array.name}</Card.Title>
              <Card.Body>
                <Card.Text>
                  <label>Race:</label> {array.race}<br />
                  <label>Gender:</label> {array.gender}<br />
                  <label>Birth:</label> {array.birth}<br />
                  <label>Spouse:</label> {array.spouse}<br />
                  <label>Death:</label> {array.death}<br />
                </Card.Text>
              </Card.Body>
              </Card.Link>
            </Card>
        ));

      setCharacter(c);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Characters">
      <Container className='jumbotron'>
        <h1 className='text-light'>Characters</h1>
        <Form className="d-flex container">
          <FormControl
            type="search"
            placeholder="Search"
            className="me-2"
            aria-label="Search"
          />
          <Button variant="primary">Search</Button>
        </Form>

        <CardGroup>
          {character}
        </CardGroup>

        {paginationBasic}
      </Container>
      <div className='img-bottom'>
        <img src={LOTRImg} alt="Tree." />
      </div>
    </main>
  );
}
