import { useEffect, useState } from 'react';
import { Container, Row, Col } from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=5cf5805fb53e011a64671582', { headers: headers });
      // const chapters = await rawChapters.json();

      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

    //   <Accordion defaultActiveKey={element._id}>
    //   <Accordion.Item eventKey={array._id}>
    //     <Accordion.Header>{element.name}</Accordion.Header>
    //     <Accordion.Body>{array.chapterName}</Accordion.Body>
    //   </Accordion.Item>
    // </Accordion>

      const c = data.docs.map(array => {
        const b = books.docs.map(element => {
          if (element._id == array.book) {
            return (
              <Row key={array._id}>
                <Col>
                  {array.chapterName}
                </Col>
                <Col>
                  {element.name}
                </Col>
              </Row>
            );
          }
        } );
        return b;
      });

      setChapter(c);

      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {chapter}
      </Container>
    </main>
  );
}
