import '../assets/characters.css';
import { useEffect, useState } from 'react';
import { FormControl, Form, CardGroup, Card, Container, Pagination, PageItem, Button } from 'react-bootstrap';
import dataChars from './e39bb0d6-2686-4edf-83fb-26915ea99483.json';
import LOTRImg from '../assets/tree-bottom.png';

export function Characters(props) {
  const [character, setCharacter] = useState([]);
  const dataLimit = 10;
  const [pages] = useState(Math.round(dataChars.total / dataLimit));
  const [page, setPage] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      //Keep commented to use local json file.
      // const rawCharacters = await fetch(props.chars + '?page=1&limit=10', { headers: props.headers });
      // const characters = await rawCharacters.json();
      setCharacter(dataChars);
    };

    fetchData();
  }, [props]);

  const pageFirst = () => {
    setPage((page) => page = 1);
    console.log("CheckedFirst: " + page);
  };
  const pagePrev = () => {
    setPage((page) => page --);
    console.log("CheckedPrev: " + page);
  };
  const pageItem = (event) => {
    const pageNumber = Number(event);
    setPage(pageNumber);
    console.log("CheckedItem: " + pageNumber);
  };
  const pageNext = () => {
    setPage((page) => page ++);
    console.log("CheckedNext: " + page);
  };
  const pageLast = () => {
    setPage((page) => page --);
    console.log("CheckedLast: " + page);
  };

  const getPaginatedData = () => {
    const start = page * dataLimit - dataLimit;
    const end = start + dataLimit;
    console.log(start, end);
    console.log(dataChars.docs.slice(start, end));
    //return dataChars.docs.slice(start, end);
  };

  const getPaginationGroup = () => {
    let start = Math.floor((page - 1) / dataLimit) * dataLimit;
    console.log(start);
    console.log(new Array(dataLimit).fill().map((_, idx) => start + idx + 1));
    //return new Array(dataLimit).fill().map((_, idx) => start + idx + 1);
  };

  return (
    <main id="Characters">
      <Container className='jumbotron'>
        <h1 className='text-light'>Characters</h1>
        <Form className="d-flex container">
          <FormControl
            type="search"
            placeholder="Search"
            className="me-2"
            aria-label="Search"
          />
          <Button variant="primary">Search</Button>
        </Form>

        <CardGroup>
          {dataChars.docs.map ((array, id) => (
            <Card key={array._id}>
              <Card.Link href={array.wikiUrl} target="_blank">
              <Card.Title>{array.name}</Card.Title>
              <Card.Body>
                <Card.Text>
                  <label>Race:</label> {array.race}<br />
                  <label>Gender:</label> {array.gender}<br />
                  <label>Birth:</label> {array.birth}<br />
                  <label>Spouse:</label> {array.spouse}<br />
                  <label>Death:</label> {array.death}<br />
                </Card.Text>
              </Card.Body>
              </Card.Link>
            </Card>
          ))}
        </CardGroup>

        <Pagination>
          <Pagination.First onClick={pageFirst} />
          <Pagination.Prev  onClick={pagePrev} />
          <Pagination.Item  onClick={pageItem(15)}>{1}</Pagination.Item>
          <Pagination.Next onClick={pageNext} />
          <Pagination.Last onClick={pageLast} />
        </Pagination>
      </Container>
      <div className='img-bottom'>
        <img src={LOTRImg} alt="Tree." />
      </div>
    </main>
  );
}
