import { useEffect, useState } from 'react';
import { Col, Container, Row } from "react-bootstrap";

export function Characters() {
  const [loading, setLoading] = useState(false);
  const [character, setChar] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchData = async () => {
      const rawChars = await fetch('https://the-one-api.dev/v2/character', {
        headers: headers
      });
      const chars = await rawChars.json();

      console.log(chars.docs);
      const char = chars.docs.map ((array) => (
        <Col key={array._id}>
          <Row>
            {array}
          </Row>
        </Col>
      ));

      setChar(char);
      setLoading(false);
    };

    fetchData();
  }, []);

  if(loading) {
    return (
      <main id="Characters">
        <h1>Characters</h1>
        <Container>
          {character}
        </Container>
      </main>
    );
  }
}
