import '../assets/characters.css';
import { useEffect, useState } from 'react';
import { FormControl, Form, CardGroup, Card, Container, Pagination, PageItem, Button } from 'react-bootstrap';
import dataChars from './e39bb0d6-2686-4edf-83fb-26915ea99483.json';
import LOTRImg from '../assets/tree-bottom.png';

export function Characters(props) {
  const [character, setCharacter] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      //Keep commented to use local json file.
      // const rawCharacters = await fetch(props.chars + '?page=1&limit=10', { headers: props.headers });
      // const characters = await rawCharacters.json();
      setCharacter(dataChars);
    };

    fetchData();
  }, [props]);

  const pageClick = () => {
    console.log("CHecked!");
  }

  return (
    <main id="Characters">
      <Container className='jumbotron'>
        <h1 className='text-light'>Characters</h1>
        <Form className="d-flex container">
          <FormControl
            type="search"
            placeholder="Search"
            className="me-2"
            aria-label="Search"
          />
          <Button variant="primary">Search</Button>
        </Form>

        <CardGroup>
          {dataChars.docs.map ((array, id) => (
            <Card key={array._id}>
              <Card.Link href={array.wikiUrl} target="_blank">
              <Card.Title>{array.name}</Card.Title>
              <Card.Body>
                <Card.Text>
                  <label>Race:</label> {array.race}<br />
                  <label>Gender:</label> {array.gender}<br />
                  <label>Birth:</label> {array.birth}<br />
                  <label>Spouse:</label> {array.spouse}<br />
                  <label>Death:</label> {array.death}<br />
                </Card.Text>
              </Card.Body>
              </Card.Link>
            </Card>
          ))}
        </CardGroup>

        <Pagination>
          <Pagination.First onClick={pageClick} />
          <Pagination.Prev  onClick={pageClick} />
          <Pagination.Item  onClick={pageClick}>{1}</Pagination.Item>
          <Pagination.Ellipsis  onClick={pageClick} />

          <Pagination.Item onClick={pageClick}>{10}</Pagination.Item>
          <Pagination.Item onClick={pageClick}>{11}</Pagination.Item>
          <Pagination.Item  onClick={pageClick} active>{12}</Pagination.Item>
          <Pagination.Item onClick={pageClick}>{13}</Pagination.Item>
          <Pagination.Item onClick={pageClick}>{14}</Pagination.Item>

          <Pagination.Ellipsis onClick={pageClick} />
          <Pagination.Item onClick={pageClick}>{20}</Pagination.Item>
          <Pagination.Next onClick={pageClick} />
          <Pagination.Last onClick={pageClick} />
        </Pagination>
      </Container>
      <div className='img-bottom'>
        <img src={LOTRImg} alt="Tree." />
      </div>
    </main>
  );
}
