import { useEffect, useState } from 'react';
import { Container, Accordion, Row, Col } from "react-bootstrap";
import data from './chapters.json';

export function Chapters() {
  const [loading, setLoading] = useState(false);
  const [chapter, setChapter] = useState(null);
  const [book, setBook] = useState(null);

  useEffect(() => {
    setLoading(true);
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer E0nN-bsSrNcMW07325tt'
    };
    const fetchDataBook = async () => {
      const rawBooks = await fetch('https://the-one-api.dev/v2/book');
      const books = await rawBooks.json();

      const b = books.docs.map((element, id) => (
        <Accordion key={id} onClick={fetchDataChapter(element._id)}>
          <Accordion.Item key={element._id}>
            <Accordion.Header>{element.name}</Accordion.Header>
            <Accordion.Body>
              {chapter}
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      ));

      setBook(b);
      setLoading(false);
    };

    const fetchDataChapter = async (idbook) => {
      console.log("worked");
      //Keep commented to use local json file.
      // const rawChapters = await fetch('https://the-one-api.dev/v2/chapter/?book=' + idbook, { headers: headers });
      // const chapters = await rawChapters.json();

      const c = data.docs.map(array => {
        if (array._id === idbook) {
          return (
            <div id='children' key={array._id}>
              {array.chapterName}
            </div>
          );
        }
      });

      setChapter(c);
    };

    fetchDataBook();
  }, []);

  if(loading) {
    return (
      <center>Loading...</center>
    );
  }

  return (
    <main id="Chapters">
      <h1>Chapters</h1>
      <Container>
        {book}

      </Container>
    </main>
  );
}
