import { useEffect, useState } from 'react';
import {Pagination, PageItem } from 'react-bootstrap';

export function PaginationAPI(props) {
  const [pages, setPages] = useState(Math.round(props.data.total / props.dataLimit));
  const [page, setPage] = useState(1);
  const pageFirst = () => {
    setPage((page) => page = 1);
    console.log("CheckedFirst: " + page);
  };
  const pagePrev = () => {
    setPage((page) => page --);
    console.log("CheckedPrev: " + page);
  };
  const pageItem = (value) => {
    const pageNumber = Number(value);
    setPage(pageNumber);
    console.log("CheckedItem: " + pageNumber);
  };
  const pageNext = () => {
    setPage((page) => page ++);
    console.log("CheckedNext: " + page);
  };
  const pageLast = () => {
    setPage((page) => page --);
    console.log("CheckedLast: " + page);
  };

  const getPaginatedData = () => {
    const start = page * props.dataLimit - props.dataLimit;
    const end = start + props.dataLimit;
    console.log(start, end);
    console.log(props.data.docs.slice(start, end));
    //return dataChars.docs.slice(start, end);
  };

  const getPaginationGroup = () => {
    let start = Math.floor((page - 1) / props.dataLimit) * props.dataLimit;
    console.log(start);
    console.log(new Array(props.dataLimit).fill().map((_, idx) => start + idx + 1));
    //return new Array(props.dataLimit).fill().map((_, idx) => start + idx + 1);
  };

  return (
    <main>
      <Pagination>
        <Pagination.First onClick={pageFirst} />
        <Pagination.Prev  onClick={pagePrev} />
        <Pagination.Item onClick={pageItem(15)}>{1}</Pagination.Item>
        <Pagination.Next onClick={pageNext} />
        <Pagination.Last onClick={pageLast} />
      </Pagination>
    </main>
  );
}
